import React, {Component} from 'react';
import './Header.css';

class Header extends Component {

    state={
      headerList:['Featured', 'Blogs', 'Bloggers', 'Search']
    };

    componentDidMount() {

    }

    render() {
        const {headerList} = this.state;

        const headerAnchor = headerList.map((item, index) => {
           return (
               <div style={styles.headerAnchorItems} className="link">
                   <span style={{padding: 10}}>{item}</span>
               </div>
           )
        });

        return (
            <div style={styles.headerContainer}>
                <div style={{display: 'flex', flexDirection: 'row'}}>
                    <span>
                        LOGO
                    </span>
                    <div style={styles.headerAnchorContainer}>
                        {headerAnchor}
                    </div>
                </div>
                <div style={styles.blogContainer}>
                    <div style={styles.headerRightAnchors}>
                        Create a Blog
                    </div>
                    <div style={styles.headerRightAnchors}>
                        Your Blogs
                    </div>
                   <div style={styles.avatar}>
S
                    </div>

                </div>
            </div>
        )
    }
}

const styles= {
    headerContainer: {
        background: '#DEDEDE',
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        padding: '10px 40px',
        justifyContent: 'space-between'
    },
    headerAnchorContainer: {
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        padding: '0 10px'
    },
    headerAnchorItems: {
        padding: '0 10px'
    },

    blogContainer: {
        display:'flex',
        flexDirection:'row',
        alignItems: 'center',
        padding:'0 10px'

    },
    headerRightAnchors: {
      padding: '0 10px'
    },
    avatar: {
        margin: '0 10px',
        display:'flex',
        justifyContent:'center',
        alignItems: 'center',
        background: 'grey',
        height: 40,
        width: 40,
        borderRadius: 20
    }

};


export default Header;
